function ScrollDown(a,b,c){this.elem=a,b||(b=$(a).attr("href")),this.scrollTo=b,c||(c=400),this.speed=c,trueSpeed=Math.abs($(window).scrollTop()-$(b).offset().top)/(c/100),this.scroll=function(){var c=$(b).offset().top;$(a).on("click",function(){$("body").animate({scrollTop:c},trueSpeed)})},this.scroll()}